def getAvailableBalance():
    balance = web3.eth.get_balance(my_wallet_address)
    humanReadable = web3.fromWei(balance,'ether')
    print(f"AvailableBalance: {humanReadable}")

def getAvailableCoin():
    token = web3.eth.contract(address=token_address, abi=tts_abi) # declaring the token contract
    token_balance = token.functions.balanceOf(my_wallet_address).call() # returns int with balance
    print(web3.fromWei(token_balance,'ether'), ' TTS')


# #Setup the PancakeSwap contract
def swapToken(spendAmmount):
    contract = web3.eth.contract(address=panRouterContractAddress, abi=panabi)
    nonce = web3.eth.get_transaction_count(my_wallet_address) #your account nonce
    
    pancakeswap2_txn = contract.functions.swapExactETHForTokens(
        0, # amount of coin you will get
        [wbnb_address,token_address], #wbnb,token address
        my_wallet_address,#receiver address
        (int(time.time()) + 10000)
    ).buildTransaction({
        'from': my_wallet_address,
        'value': web3.toWei(spendAmmount,'ether'),#The ammount you want to spend
        'gas': 300000,
        'gasPrice': web3.toWei('5','gwei'),
        'nonce': nonce,
    })
        
    signed_txn = web3.eth.account.sign_transaction(pancakeswap2_txn, private_key=private_key) #sign it with your private key
    tx_token = web3.eth.send_raw_transaction(signed_txn.rawTransaction) #send the signed transaction 
    hex_tx = (web3.toHex(tx_token)) #convert it into hex
    print(hex_tx)
    return hex_tx

def getTxDetail(tx_token):
    receipt = (web3.eth.waitForTransactionReceipt(tx_token)) #wait for transaction to compelete and if 1 success
    if(receipt['status'] == 1):
        print("Swap Successfully")
    else:
        print("Swap Unsuccessfully")

